mapper.select:
select 
from JiaoCaiDistribute


    and id = #{id,jdbcType=NVARCHAR}


    and issuenumber = #{issuenumber}


    and subcode = #{subcode}


    and addwho = #{addwho}


    and type = #{type}


    and computeno = #{computeno}


    and shipno = #{shipno}


    and pickno = #{pickno}


    and batchno = #{batchno}


    and status = #{status}



    and convert(varchar(20),adddate,20) like #{adddate}


order by adddate desc
