mapper.select:
select 

from JiaoCaiPack
where subcode = #{subcode,jdbcType=NVARCHAR}
  and pack = #{pack,jdbcType=INTEGER}
  and issuenumber = #{issuenumber,jdbcType=NVARCHAR}

mapper.delete:
delete from JiaoCaiPack
where subcode = #{subcode,jdbcType=NVARCHAR}
  and pack = #{pack,jdbcType=INTEGER}
  and issuenumber = #{issuenumber,jdbcType=NVARCHAR}

mapper.update:
update JiaoCaiPack

  
    id = #{id,jdbcType=INTEGER},
  

where subcode = #{subcode,jdbcType=NVARCHAR}
  and pack = #{pack,jdbcType=INTEGER}
  and issuenumber = #{issuenumber,jdbcType=NVARCHAR}

update JiaoCaiPack
set id = #{id,jdbcType=INTEGER}
where subcode = #{subcode,jdbcType=NVARCHAR}
  and pack = #{pack,jdbcType=INTEGER}
  and issuenumber = #{issuenumber,jdbcType=NVARCHAR}
