mapper.select:
select 

from JiaoCaiStorer
where id = #{id,jdbcType=INTEGER}
  and storerkey = #{storerkey,jdbcType=VARCHAR}

mapper.delete:
delete from JiaoCaiStorer
where id = #{id,jdbcType=INTEGER}
  and storerkey = #{storerkey,jdbcType=VARCHAR}

mapper.select:
select 
from JiaoCaiStorer

  
    and id = #{id,jdbcType=NVARCHAR}
  
  
    and type = #{type}
  
  
    and phone = #{phone}
  
  
    and shortname like CONCAT('%',#{shortname},'%')
  
  
    and address1 like CONCAT('%',#{address1},'%')
  
  
    and contact like CONCAT('%',#{contact},'%')
  

order by shortname
